export default class Yogaexercises extends Component {
	state = {
		user: null
	}

	componentDidMount() {
		fetch('http://localhost:3000/poses')
			.then(res => res.json())
			.then(data => {
				this.setState({
					user: data
				})
			})
	}

	render() {
		return ( 
			<Layout title="Yoga Exercises">
		<h1>Exercises for beginners</h1>
		<p>List out the exercises here, pull from API</p>
		{/* {console.log(typeof props)} */}
		{console.log(props.poses)}
		<ul>
			{props.poses.map((pose) => (
				<li key={pose.id}>
					<Link href="/p/[id]" as={`/p/${pose.id}`}>
						<a>{pose.name}</a>
					</Link>
					<p>{pose.category}</p>
				</li>
			))}
		</ul>
	</Layout>
		)
	}
}
