import Layout from '../components/MyLayout';
import Login from './login';
// import Link from 'next/link';
import fetch from 'isomorphic-unfetch';
import Head from 'next/head';

export default class Index extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			currentUser : '',
			delete      : false
		};
	}

	userState = (user) => {
		this.setState(
			{
				currentUser : user
			},
			() => {
				console.log('user logged in');
			}
		);
	};

	toLogout = () => {
		this.setState({ currentUser: '' });
	};

	toDelete = () => {
		this.setState({ delete: true });
	};

	render() {
		return (
			<Layout title="Home">
				<Head>
					<title>Your Yoga Journey</title>
					<link
						rel="stylesheet"
						href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css"
					/>
				</Head>
				{/* Jumbo design  */}
				<div className="jumbo1">
					<div className="container">
						<h1 className="jumbo1words">New to yoga and so lost with all the jargon?</h1>
						<h1 className="words">
							We've been there! With Yoga+'s Kickstarter Package, you can now learn systematically and
							track your progress!{' '}
						</h1>
					</div>
				</div>

				<div className="jumbo2">
					<div className="container card-space ">
						<div className="row">
							{/* Left-side  */}
							<img
								src="https://scontent.fsin3-1.fna.fbcdn.net/v/t1.0-9/15741122_393065377698266_6683392729503309587_n.jpg?_nc_cat=104&_nc_oc=AQlHaPkh7JNgJ_mGtO_Dr3qjGPfSHph9E5R1-MyVXVD64FcOxSSzTQieUHDj3NxiWIA&_nc_ht=scontent.fsin3-1.fna&oh=0dc8bff00ea1da689b46083b9ed2a32a&oe=5EF9F0CD"
								className="col-sm float-left leftImage"
							/>

							{/* Right Side  */}
							<div className="col-sm rightImage card">
								{/* Sign Up */}
								<img
									className="card-img-top logo"
									src="data:image/png;base64,"
								/>
								<form onSubmit={this.handleSubmit} className="form-signin">
									<img className="mb-4 logo" src="" alt="" width="120" height="120" />
									<h6 className="h3 mb-3">Your Yoga Journey Begins here!</h6>
									<label htmlFor="username" className="sr-only">
										Username
									</label>
									<br />
									<input
										type="username"
										id="username"
										name="username"
										className="form-control"
										placeholder="Username"
										required=""
										autoFocus=""
										value={this.state.username}
										onChange={this.handleChange}
									/>

									<label htmlFor="password" className="sr-only">
										Password
									</label>
									<input
										type="password"
										id="password"
										name="name"
										className="form-control"
										placeholder="Password"
										required=""
										value={this.state.password}
										onChange={this.handleChange}
									/>

									<button className="btn btn-lg btn-primary btn-block" type="submit">
										Sign in
									</button>
								</form>
							</div>
						</div>
					</div>
				</div>
				{/* {this.state.currentUser ? (
					<Kickstarterpath currentUser={this.state.currentUser} />
				) : (
					<Login userState={this.userState} />
				)} */}
				{/* <Kickstarterpath currentUser={this.state.currentUser} />{' '} */}
				{/* <img src="/images/test.jpg" /> */}
				<style jsx>{`
					.col-sm {
						height: 700px;
						width: 100%;
						// border: black 1px solid;
						// background-color: grey;
					}

					.card-space {
						margin-top: 3%;
					}

					.leftImage {
						max-width: 50%;
						object-fit: cover;
						margin-right: 10px;
						padding: 5px;
						object-position: 10% 10%;
					}

					.rightImage {
						// padding: 20px;
						// border: 2px solid red;
						// margin: 5px;
						box-shadow: 5px 5px 20px -10px rgba(0, 0, 0, 0.55);
					}

					.card {
						padding: 4%;
						text-align: center;
					}

					.card-img-top {
						width: 40%;
						display: block;
						margin: 0 auto;
					}

					.jumbo1 {
						background: linear-gradient(rgba(202, 202, 202, 0.3), rgba(0, 0, 0, 0.5)),
							url('https://scontent.fsin3-1.fna.fbcdn.net/v/t1.0-9/15027659_370613736610097_1460303554607900122_n.jpg?_nc_cat=107&_nc_oc=AQkRlwlDC8IaXGsrKrbSgPD8PlcVEtkkAOwd85OjpLSUTM2YMIOLbpA1FSghhB0atmg&_nc_ht=scontent.fsin3-1.fna&oh=b01baf74485a23d695d953a594aa990f&oe=5EC58659');
						background-size: cover;
						background-position: 110% 110%;
						/* opacity: 80%; */
						height: 800px;
						padding-top: 20%;
						padding-bottom: 20%;
					}

					.jumbo2 {
						background: linear-gradient(rgba(0, 0, 0, 0.3), rgba(0, 0, 0, 0.3));
						// url('https://scontent.fsin3-1.fna.fbcdn.net/v/t1.0-9/14232395_334076126930525_1838539352840814452_n.jpg?_nc_cat=108&_nc_oc=AQmYC28rO5jcHVnjhRieDreQdEupoXgQvS1QmADmsGhtYL05RJ8ZJSL9YBT_JYYV6CA&_nc_ht=scontent.fsin3-1.fna&oh=006b9723332e587397f1432922104e79&oe=5EF5447B');
						// background-size: cover;
						// background-position: 110% 110%;
						// /* opacity: 80%; */
						// height: 800px;
						padding-top: 3%;
						// padding-bottom: 20%;
					}

					.jumbo1words {
						color: white;
						font-family: "Permanent Marker", cursive;
					}

					.words {
						display: block;
						max-width: 900px;
						margin-top: 10px;
						font-size: 1.3rem;
						color: black;
					}
				`}</style>
			</Layout>
		);
	}
}
