import Header from './Header';

const layoutStyle = {
	margin  : 20,
	padding : 20,
	border  : '1px solid #DDD'
};

const Layout = (props, title) => (
	<div style={layoutStyle}>
		<Header />
		{props.title}
		{props.children}
		<footer>footer (layout component)</footer>
	</div>
);

export default Layout;
