// import Header from '../components/Header';
import Layout from '../components/MyLayout';
import Link from 'next/link';
// import PrivateClass from '../components/PrivateClass';
import PrivateClass from './PrivateClass';
import PublicClass from './PublicClass';
import { Component } from 'react';
import Router, { useRouter } from 'next/router';
import Head from 'next/head';
// import Axios from 'axios';

export default class Kickstarterpath extends Component {
	constructor(props) {
		super(props);
		this.state = {
			username                : 'customer',
			bookedPrivateFirstClass : false,
			bookedFirstPublicClass  : false
		};
	}

	// componentDidMount() {
	// 	let token = "Bearer" + localStorage.getItem("jwt");
	// 	Axios({method: 'get', url: '/api/poses', headers: { 'Authorization': token }})
	// 		.then(response => {
	// 			this.setState({ poses: response.data})
	// 		}).catch(error => console.log('error', error))
	// }

	// componentDidMount = () => {
	// 	// console.log('Hello', bookedPrivateFirstClass);
	// 	this.setState({
	// 		bookedPrivateFirstClass : true
	// 	});
	// };

	// To redirect to private class
	toPrivateClass = () => {
		console.log('Button has been clicked');
		console.log("Button's state is", this.state.bookedPrivateFirstClass);
		this.setState({
			bookedPrivateFirstClass : true
		});
		Router.push('/PrivateClass');
		// <PrivateClass bookedFirstClass={this.bookedFirstClass} />;
	};

	// To redirect to public class
	toPublicClass = () => {
		console.log('Button has been clicked');
		console.log("Button's state is", this.state.bookedFirstPublicClass);
		this.setState({
			bookedPrivateFirstClass : true
		});
		Router.push('/PrivateClass');
		// <PrivateClass bookedFirstClass={this.bookedFirstClass} />;
	};

	render() {
		const bookedPrivateFirstClass = this.state.bookedPrivateFirstClass;

		return (
			<Layout title="Kickstarter Profile">
				<Head>
					<title>Your Journey Begins here</title>
					<link
						rel="stylesheet"
						href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css"
						integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T"
						crossorigin="anonymous"
					/>
				</Head>

				{/* <button onClick ={() => this.setState({showDetails: true})} className="class-box">Private Class</button> */}
				<div className="card-space">
					<h1>Your Kickstarter pathway</h1>
					<h3>Hi {this.state.username}, welcome to your kickstarter pathway profile.</h3>
					<div className="container">
						<div className="row ">
							{/* First section  */}

							<div className="col-sm special">
								<div onClick={this.toPrivateClass}>
									<h3>Private Class</h3>
									<p>Book your initial assessment private class with one of our founders</p>
									<br />
									<p>Insert image?</p>
									{bookedPrivateFirstClass ? <h1>BOOKED</h1> : <h1>NOT BOOKED</h1>}
								</div>
							</div>

							{/* First section  */}
							<div className="col-sm special">
								<Link href="/PublicClass">
									<div onClick={this.toPublicClass}>
										<h3>Public Class</h3>
										<p>Book your public class</p>
										<p>Recap and review your lesson</p>
										<br />
										<p>Insert image?</p>
									</div>
								</Link>
							</div>

							{/* First section  */}
							<div className="col-sm special">
								<h3>PublicClass</h3>
							</div>
							{/* First section  */}
							<div className="col-sm special">
								<h3>PublicClass</h3>
							</div>
							{/* First section  */}
							<div className="col-sm special">
								<h3>PublicClass</h3>
							</div>
							{/* First section  */}
							<div className="col-sm special">
								<h3>PublicClass</h3>
							</div>
							{/* First section  */}
							<div className="col-sm special">
								<h3>Private Class</h3>
							</div>
						</div>
					</div>

					{/* <ul className="">
						<li className="">
							<Link href="/PrivateClass">
								<button onClick={this.toPrivateClass} className="class-box">
									Private Class
								</button>
							</Link>
						</li>

						<li className="">
							<Link href="/PrivateClass">
								<button onClick={this.toPrivateClass} className="class-box">
									Private Class
								</button>
							</Link>
						</li>
					</ul> */}
				</div>

				{/* <Link href="/PrivateClass">
					<button onClick={this.toPrivateClass} className="class-box">
						Private Class
					</button>
				</Link>
				<br />
				<Link href="/PublicClass">
					<button onClick={this.toPublicClass} className="class-box">
						Public Class
					</button>
				</Link> */}

				<style jsx>
					{`
						.card-space {
							// margin-top: 50px;
							padding-top: 5%;
							padding-bottom: 5%;
						}

						.special {
							border: 1px solid blue;
							padding: 10px;
						}

						* {
							text-align: center;
						}

						p {
							font-size: 12px;
						}

						h3 {
							font-size: 20px;
						}
					`}
				</style>
			</Layout>
		);
	}
}
