// import Header from '../components/Header';
import Layout from '../components/MyLayout';
import Head from 'next/head';

export default function About() {
	return (
		<Layout title="About us">
			<Head>
				<title>About</title>
				<link
					rel="stylesheet"
					href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css"
				/>
			</Head>
			{/* <div className="jumbo1">
				<div className="container">
					<h1 className="jumbo1words">Come join us!</h1>
				</div>
			</div> */}
			<div className="container card-space">
				<div className="row">
					<div className="col-sm">
						<h1>The Kickstarter Package</h1>
						<p>
							We've all been there - we want to indulge in our yoga practice but there just isn't a
							systematic pathway for us to consolidate our learnings! We didn't even know which classes
							were suitable for us so we end up picking more advanced classes. We begin to doubt ourselves
							when we fail to execute more advanced poses and slowly, we lose interest. We created this
							Kickstarter Package to track your progress when you are initially starting out in the
							amazing world of Yoga. Your package consists of 2 private sessions and 4 public classes.
						</p>
						<h1>Yoga+</h1>
						<p>
							At Yoga+, most of the yoga happens outside of the yoga mat. Through our regular yoga
							classes, monthly workshops and other activities, no matter whether you are the absolute
							beginner or the advanced practitioner, we invite you to explore the infinite possibilities
							beyond the yoga asanas.
						</p>
					</div>
					<div className="col-sm">Image</div>
				</div>
			</div>

			<style jsx>
				{`
					.card-space {
						// margin-top: 50px;
						padding-top: 5%;
						padding-bottom: 5%;
						text-align: center;
						// display: inline-block;
					}

					li {
						list-style: none;
					}
					.jumbo1 {
						background: linear-gradient(rgba(0, 0, 0, 0.2), rgba(0, 0, 0, 0.1)),
							url('https://i0.wp.com/www.yogaplus.sg/wp-content/uploads/2019/07/171013-171013-First-Cut-Yoga-Arab-St-2.0-1-1.jpg?w=2520&ssl=1');
						background-size: cover;
						// background-position: 10% 30%;
						/* opacity: 80%; */
						min-height: 300px;
						padding-top: 20%;
						padding-bottom: 20%;
					}

					.jumbo1words {
						color: white;
						font-family: "Permanent Marker";
					}
				`}
			</style>
		</Layout>
	);
}
