import { post } from 'axios';

class LoginForm extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			username    : '',
			password    : '',
			currentUser : '',
			message     : '',
			redirect    : false
		};
	}

	handleChange = (event) => {
		this.setState({ [event.target.id]: event.target.value });
	};

