import Layout from '../components/MyLayout';
import Login from './login';
// import Link from 'next/link';
import fetch from 'isomorphic-unfetch';
import Head from 'next/head';

export default class Index extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			currentUser : '',
			delete      : false
		};
	}

	userState = (user) => {
		this.setState(
			{
				currentUser : user
			},
			() => {
				console.log('user logged in');
			}
		);
	};

	toLogout = () => {
		this.setState({ currentUser: '' });
	};

	toDelete = () => {
		this.setState({ delete: true });
	};

	render() {
		return (
			<Layout title="Home">
				<Head>
					<title>Your Yoga Journey</title>
					<link
						rel="stylesheet"
						href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css"
						integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T"
						crossOrigin="anonymous"
					/>
				</Head>
				<div className="container card-space">
					<div className="row">
						{/* Left-side  */}
						<img
							src="https://media.timeout.com/images/104103651/630/472/image.jpg"
							className="col-sm float-left leftImage"
						/>

						{/* Right Side  */}
						<div className="col-sm rightImage">
							{/* Sign Up */}
							<form onSubmit={this.handleSubmit} className="form-signin">
								<img
									className="mb-4"
									src="data:image/png;base64,"
									alt=""
									width="120"
									height="120"
								/>
								<h1 className="h3 mb-3 font-weight-normal">Please sign in</h1>
								<label htmlFor="username" className="sr-only">
									Username
								</label>
								<input
									type="username"
									id="username"
									name="username"
									className="form-control"
									placeholder="Username"
									required=""
									autoFocus=""
									value={this.state.username}
									onChange={this.handleChange}
								/>
								<label htmlFor="password" className="sr-only">
									Password
								</label>
								<input
									type="password"
									id="password"
									name="name"
									className="form-control"
									placeholder="Password"
									required=""
									value={this.state.password}
									onChange={this.handleChange}
								/>

								<button className="btn btn-lg btn-primary btn-block" type="submit">
									Sign in
								</button>
							</form>
						</div>
					</div>
				</div>

				{/* Jumbo design  */}
				{/* <div className="jumbo1">
					<div className="container">
						<h1 className="display-4 jumbo1words">Image</h1>
					</div>
				</div> */}

				{/* {this.state.currentUser ? (
					<Kickstarterpath currentUser={this.state.currentUser} />
				) : (
					<Login userState={this.userState} />
				)} */}
				{/* <Kickstarterpath currentUser={this.state.currentUser} />{' '} */}

				{/* <img src="/images/test.jpg" /> */}
				<style jsx>{`
					.col-sm {
						height: 700px;
						width: 100%;
						// border: black 1px solid;
						// background-color: grey;
					}

					.card-space {
						margin-top: 3%;
					}

					// .leftImage {
					// 	margin-right: 10px;
					// 	padding: 5px;
					// }

					.rightImage {
						// display: block;
						position: absolute
						padding: 30px;
						border: 2px solid red;
						margin: 5px;
						// box-shadow: 9px 7px 25px -10px rgba(0, 0, 0, 0.75);
					}

					img {
						object-fit: cover;
						object-position: 0 70%;
					}

					.leftImage {
						margin-right: 10px;
						padding: 5px;
					}

					// .jumbo1 {
					// 	background: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5));
					// 	background-size: cover;
					// 	background-position: 10% 30%;
					// 	/* opacity: 80%; */
					// 	min-height: 300px;
					// 	padding-top: 20%;
					// 	padding-bottom: 20%;
					// }

					// .jumbo1words {
					// 	color: white;
					// 	font-family: "Permanent Marker";
					// }
				`}</style>
			</Layout>
		);
	}
}
