// import { loginUser } from '../lib/auth.js';
import fetch from 'isomorphic-unfetch'; // For fetch API
import Head from 'next/head';
import { Component } from 'react';
import Link from 'next/link';
import Layout from '../components/MyLayout';
import { post } from 'axios';

class LoginForm extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			username    : '',
			password    : '',
			currentUser : '',
			message     : ''
		};
	}

	handleChange = (event) => {
		this.setState({ [event.target.name]: event.target.value });
	};

	handleSubmit = (event) => {
		event.preventDefault();
		fetch('/sessions', {
			body    : JSON.stringify(this.state),
			method  : 'POST',
			headers : {
				Accept         : 'application/json, text/plain, */*',
				'Content-Type' : 'application/json'
			}
		})
			.then((loggedInUser) => {
				return loggedInUser.json();
			})
			.then((jsonedUser) => {
				if (jsonedUser.message === null) {
					this.setState({
						message : 'User cannot be found'
					});
				} else if (jsonedUser.message === false) {
					this.setState({
						message : 'Wrong Password'
					});
				} else {
					console.log(jsonedUser);
					this.setState({
						currentUser : jsonedUser
					});
					console.log('Current user is: ', this.state.currentUser);
				}
			})
			.then(() => {
				this.props.userState(this.state.currentUser);
			})
			.catch((error) => console.log(error));
	};

	// handleSubmit = event => {
	//     event.preventDefault();

	//     // const { username, password } = this.state;
	//     // console.log(this.state);

	//     const username = document.getElementById('username').value;
	//     const password = document.getElementById('password').value;

	//     const request = {"auth": {"username": username, "password": password }};

	//     post('/api/user_token', request)
	//         .then(response => {
	//             localStorage.setItem("jwt", response.data.jwt);
	//             this.props.history.push("/")
	//         })
	//         .catch(error => console.log("Login erro", error));

	//     // loginUser(username, password)
	// }

	render() {
		return (
			<Layout title="Log in Page">
				<Head>
					<title>Review your class</title>
					<link
						rel="stylesheet"
						href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css"
						integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T"
						crossorigin="anonymous"
					/>
				</Head>

				<div className="bodyCenter">
					{/* <form onSubmit={this.handleSubmit}>
                    <div>
                        <input type="email" name="email" placeholder="email" onChange={this.handleChange}/>
                    </div>
                    <div>
                        <input type="password" name="password" placeholder="password" onChange={this.handleChange}/>
                    </div>
                    <button type="submit">Submit</button>

                </form>     */}

					<form onSubmit={this.handleSubmit} className="form-signin">
						<img
							className="mb-4"
							src="data:image/png;base64,"
							alt=""
							width="120"
							height="120"
						/>
						<h1 className="h3 mb-3 font-weight-normal">Please sign in</h1>
						<label htmlFor="username" className="sr-only">
							Username
						</label>
						<input
							type="username"
							id="username"
							className="form-control"
							placeholder="Username"
							required=""
							autoFocus=""
							value={this.state.username}
							onChange={this.handleChange}
						/>
						<label htmlFor="password" className="sr-only">
							Password
						</label>
						<input
							type="password"
							id="password"
							className="form-control"
							placeholder="Password"
							required=""
							value={this.state.password}
							onChange={this.handleChange}
						/>

						<button className="btn btn-lg btn-primary btn-block" type="submit">
							Sign in
						</button>
					</form>
				</div>

				<style jsx>{`
					.bodyCenter {
						width: 350px;
						display: block;
						margin: 0 auto;
						padding-top: 5%;
						padding-bottom: 5%;
						text-align: center;
					}
				`}</style>
			</Layout>
		);
	}
}

export default LoginForm;
