import Layout from '../components/MyLayout';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';
import Error from './_error';
import Head from 'next/head';
import { Component } from 'react';
import classSchedule from '../base/mindbodyonline.js';

// import useSWR from 'swr';

export default class Mindbodyonline extends Component {
	constructor(props) {
		super(props);
		this.state = {
			schedule : ''
		};
	}

	// To fetch
	static async getInitialProps() {
		const res = await fetch('http://localhost:3000/classes');
		const statusCode = res.status > 200 ? res.status : false;
		const data = await res.json();
		console.log(`Class schedule fetch.`);

		return {
			schedule   : data,
			statusCode
		};
	}

	render() {
		const { schedule, statusCode } = this.props;

		if (statusCode) {
			return <Error statusCode={statusCode} />;
		}

		return (
			<Layout title="Class Schedule">
				<Head>
					<title>Class Schedule</title>
					<link
						rel="stylesheet"
						href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css"
						integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T"
						crossorigin="anonymous"
					/>
				</Head>

				<div className="card-space">
					{/* <div className="container"> */}
					{/* {console.log(schedule)} */}
					{/* {console.log(schedule.Classes)} */}

					{classSchedule.map((x) => (
						<li key={x.classScheduleId}>
							<div className="eachListing container">
								<p>Studio: {x.Location.Name}</p>
								<p>Class Name: {x.classDescription.Name}</p>
								<p>Class Description: {x.ClassDescription.Description}</p>
								<p>Date: {x.Date}</p>
								<p>
									{x.StartDateTime} to {x.EndDateTime}
								</p>
								<p>Availability: {x.IsAvailable}</p>
								<a href={'https://clients.mindbodyonline.com/LoginLaunch?studioid=590475'}>
									Book class
								</a>
							</div>
						</li>
					))}
					{/* MBO  */}
					{/* {schedule.Classes.map((x) => (
						<li key={x.ClassScheduleId}>
							<div className="eachListing container">
								<p>Studio: {x.Location.Name}</p>
								<p>Class Description: {x.ClassDescription.Description}</p>
								<p>Start-date: {x.StartDateTime}</p>
								<p>End date: {x.EndDateTime}</p>
								<p>Availability: {x.IsAvailable}</p>
								<Link href="www.manutd.com">
								<a href={'https://clients.mindbodyonline.com/LoginLaunch?studioid=590475'}>
									Book class
								</a>
								</Link>
							</div>
						</li>
					))} */}
					{/* {console.log(schedule[1].ClassDescription)} */}
					{/* <Link href="https://clients.mindbodyonline.com/LoginLaunch?studioid=590475"> */}
					{/* </div> */}
					{/* <div className="container ">
						<Link href="https://clients.mindbodyonline.com/LoginLaunch?studioid=590475">
							<h1>Book class</h1>
						</Link>
					</div> */}
				</div>

				<style jsx>
					{`
						.card-space {
							// margin-top: 50px;
							padding-top: 5%;
							padding-bottom: 5%;
							text-align: center;
							// display: inline-block;
						}

						.eachListing {
							border: 1px solid blue;
							margin-bottom: 5px;
						}

						li {
							list-style: none;
						}
					`}
				</style>
			</Layout>
		);
	}
}

