import fetch from 'isomorphic-unfetch'; // For fetch API

export default class CommentsForm extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			comments : [],
			username : ''
		};
	}

	// handleSubmitComments = (e) => {
	// 	e.preventDefault();
