import fetch from 'isomorphic-unfetch'; // For fetch API
import Head from 'next/head';
import { Component } from 'react';
import Link from 'next/link';
import Layout from '../components/MyLayout';
import Kickstarterpath from './Kickstarterpath';

export default class Login extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			username    : '',
			password    : '',
			currentUser : '',
			message     : '',
			redirect    : false
		};
	}

	static async getInitialProps() {
		const res = await fetch('http://localhost:3000/users');
		// const statusCode = res.status > 200 ? res.status : false;
		const data = await res.json();
		console.log(`User data fetched. Count: ${data.length}`);

		return {
			// poses : data.map((entry) => entry.pose)
			users : data
		};
	}

	handleChange = (event) => {
		this.setState({ [event.target.id]: event.target.value });
	};

	handleSubmit = (event) => {
		event.preventDefault();
		fetch('http://localhost:3000/users', {});
	};

	render() {
		// if (this.state.redirect === true) {
		// 	return <Redirect to="/kickstarter" />;
		// }
		// console.log(this.state.user, this.state.password);
		return (
			<Layout title="Log in Page">
				<Head>
					<title>Review your class</title>
					<link
						rel="stylesheet"
						href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css"
						integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T"
						crossorigin="anonymous"
					/>
				</Head>

				<div className="bodyCenter">
					<form onSubmit={this.handleSubmit} className="form-signin">
						<img
							className="mb-4"
							src="data:image/png;base64,"
							alt=""
							width="120"
							height="120"
						/>
						<h1 className="h3 mb-3 font-weight-normal">Please sign in</h1>
						<label htmlFor="username" className="sr-only">
							Username
						</label>
						<input
							type="username"
							id="username"
							name="username"
							className="form-control"
							placeholder="Username"
							required=""
							autoFocus=""
							value={this.state.username}
							onChange={this.handleChange}
						/>
						<label htmlFor="password" className="sr-only">
							Password
						</label>
						<input
							type="password"
							id="password"
							name="name"
							className="form-control"
							placeholder="Password"
							required=""
							value={this.state.password}
							onChange={this.handleChange}
						/>

						<button className="btn btn-lg btn-primary btn-block" type="submit">
							Sign in
						</button>
					</form>
				</div>

				<style jsx>{`
					.bodyCenter {
						width: 350px;
						display: block;
						margin: 0 auto;
						padding-top: 5%;
						padding-bottom: 5%;
						text-align: center;
					}
				`}</style>
			</Layout>
		);
	}
}
