			<Layout title="Kickstarter Profile">
				<Head>
					<title>Your Journey Begins here</title>
					<link
						rel="stylesheet"
						href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css"
						integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T"
						crossorigin="anonymous"
					/>
				</Head>


				{/* <button onClick ={() => this.setState({showDetails: true})} className="class-box">Private Class</button> */}



				<div className="card-space">
					<h1>Your Kickstarter pathway</h1>
					<p>Hi user, welcome to your....</p>

					<div className="container">
						<div className="row ">
							{/* First section  */}
							<div className="col-sm special">
							<Link href="/PrivateClass">
								<button onClick={this.toPrivateClass}>
								<h3>Private Class</h3>
								<p>Book your initial assessment private class with one of our founders</p>								</button>
							</Link>


							</div>
							{/* First section  */}
							<div className="col-sm special">
								Public Class
							</div>
							{/* First section  */}
							<div className="col-sm special">
								Public Class
							</div>
							{/* First section  */}
							<div className="col-sm special">
								Public Class
							</div>
							{/* First section  */}
							<div className="col-sm special">
								Public Class
							</div>
							{/* First section  */}
							<div className="col-sm special">
							<h3>PublicClass</h3>
								<p>Book your initial assessment private class with one of our founders</p>								
							</div>
							{/* First section  */}
							<div className="col-sm special">
								<h3>Private Class</h3>
								<p>Book your initial assessment private class with one of our founders</p>	
							</div>


						</div>
					</div>



					<ul className="">
						<li className="">
							<Link href="/PrivateClass">
								<button onClick={this.toPrivateClass} className="class-box">
									Private Class
								</button>
							</Link>
						</li>

						<li className="">
							<Link href="/PrivateClass">
								<button onClick={this.toPrivateClass} className="class-box">
									Private Class
								</button>
							</Link>
						</li>
					</ul>
				</div>

				{/* <Link href="/PrivateClass">
					<button onClick={this.toPrivateClass} className="class-box">
						Private Class
					</button>
				</Link>
				<br />
				<Link href="/PublicClass">
					<button onClick={this.toPublicClass} className="class-box">
						Public Class
					</button>
				</Link> */}

				<style jsx>
					{`
						.card-space {
							margin-top: 50px;
						}

						.special {
							border: 1px solid blue;
						}

						* { 
							text-align: center;
						}

						p {
							font-size: 10px;
						}

						h3 {
							font-size: 20px;
						}
					`}
				</style>
			</Layout>
		);
	}
}
