import Layout from '../components/MyLayout';
import Link from 'next/link';
import { Component } from 'react';
import Head from 'next/head';
import fetch from 'isomorphic-unfetch'; // For fetch API

export default class CommentsForm extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			comments      : [],
			comment       : '',
			commentOnUser : '',
			username      : ''
		};
	}

	componentDidMount = () => {
		this.getComments();
	};

	getComments = () => {
		fetch('http://localhost:3000/comments')
			.then((response) => response.json())
			.then((json) => console.log(json))
			.catch((error) => console.error(erro));
	};

	handleSubmitComments = (e) => {
		e.preventDefault();
		console.log(this.state.comments);
		fetch('http://localhost:3000/comments', {
			body    : JSON.stringify({ comment: this.state.comment }),
			method  : 'POST',
			headers : {
				Accept         : 'application/json, text/plain, */*',
				'Content-Type' : 'application/json'
			}
		})
			.then((createdComment) => {
				return createdComment.json();
			})
			.then((jsonedComment) => {
				this.setState({
					comments : [jsonedComment, ...this.state.comments]
				});
				console.log(comments);
			})
			.catch((error) => console.log(error));
	};

	handleChange = (e) => {
		this.setState({ comment: e.target.value });
	};

	render() {
		return (
			<div className="container">
				<Head>
					{/* <title>Your public class</title>  */}
					<link
						rel="stylesheet"
						href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css"
						integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T"
						crossorigin="anonymous"
					/>
				</Head>
				<div className="row">
					<div className="col-sm">
						<form onSubmit={this.handleSubmitComments}>
							<label>
								<h6>Comments</h6>
								<textarea value={this.state.comments} onChange={this.handleChange} />
								{/* FOR USERNAME BLABLA */}
							</label>
							<br />
							<input className="btn btn-success" type="submit" value="Submit" />
						</form>
						<div className="col-sm">
							<h1>{this.state.comments}</h1>
						</div>
					</div>
				</div>
			</div>
		);
	}
}
