import Layout from '../components/MyLayout';
import Login from './login';
// import Link from 'next/link';
import fetch from 'isomorphic-unfetch';
import Head from 'next/head';

export default class Index extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			currentUser : '',
			delete      : false
		};
	}

	userState = (user) => {
		this.setState(
			{
				currentUser : user
			},
			() => {
				console.log('user logged in');
			}
		);
	};

	toLogout = () => {
		this.setState({ currentUser: '' });
	};

	toDelete = () => {
		this.setState({ delete: true });
	};

	render() {
		return (
			<Layout title="Home">
				<Head>
					<title>Your Yoga Journey</title>
					<link
						rel="stylesheet"
						href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css"
					/>
				</Head>
				{/* Jumbo design  */}
				<div className="jumbo1">
					<div className="container">
						<h1 className="jumbo1words">Pick up </h1>
						<h1 className="words">We've been there!</h1>
					</div>
				</div>

				<div className="container card-space">
					<div className="row">
						{/* Left-side  */}
						<img
							src="https://scontent.fsin3-1.fna.fbcdn.net/v/t31.0-8/22904806_541047162900086_657467607020809544_o.jpg?_nc_cat=104&_nc_oc=AQm4HCIbL9oN20balW-e4roJDj3WY2PKVrWTEVE4aQcCsNnNq3hrJGybmznqyHzPX-k&_nc_ht=scontent.fsin3-1.fna&oh=7a5458c13948255094446387fceb7c5b&oe=5EC1989F"
							className="col-sm float-left leftImage"
						/>

						{/* Right Side  */}
						<div className="col-sm rightImage card">
							{/* Sign Up */}
							<img
								className="card-img-top logo"
								src="https://scontent.fsin3-1.fna.fbcdn.net/v/t1.0-9/14184318_332158770455594_5764085729526117445_n.jpg?_nc_cat=110&_nc_oc=AQmCmTWh-76RiYXNvaJ60MSOea4blINH4IiaCbdmSYhSXByWhqnSG9pLVe8AS5kZpRg&_nc_ht=scontent.fsin3-1.fna&oh=74587a8bade9ccd34f1bd9418bd89ac3&oe=5EF42BAC"
								width="120"
								height="120"
							/>
							<form onSubmit={this.handleSubmit} className="form-signin">
								<img className="mb-4 logo" src="" alt="" width="120" height="120" />
								<h6 className="h3 mb-3">Your Yoga Journey Begins here!</h6>
								<label htmlFor="username" className="sr-only">
									Username
								</label>
								<br />
								<input
									type="username"
									id="username"
									name="username"
									className="form-control"
									placeholder="Username"
									required=""
									autoFocus=""
									value={this.state.username}
									onChange={this.handleChange}
								/>

								<label htmlFor="password" className="sr-only">
									Password
								</label>
								<input
									type="password"
									id="password"
									name="name"
									className="form-control"
									placeholder="Password"
									required=""
									value={this.state.password}
									onChange={this.handleChange}
								/>

								<button className="btn btn-lg btn-primary btn-block" type="submit">
									Sign in
								</button>
							</form>
						</div>
					</div>
				</div>
				{/* {this.state.currentUser ? (
					<Kickstarterpath currentUser={this.state.currentUser} />
				) : (
					<Login userState={this.userState} />
				)} */}
				{/* <Kickstarterpath currentUser={this.state.currentUser} />{' '} */}
				{/* <img src="/images/test.jpg" /> */}
				<style jsx>{`
					.col-sm {
						height: 700px;
						width: 100%;
						// border: black 1px solid;
						// background-color: grey;
					}

					.card-space {
						margin-top: 3%;
					}

					.logo {
						display: block;
						margin: 0 auto;
					}

					.leftImage {
						max-width: 50%;
						object-fit: cover;
					}

					.rightImage {
						// display: block;
						// position: absolute;
						top: 50%;
						padding: 30px;
						// border: 2px solid red;
						margin: 5px;
						box-shadow: 9px 7px 25px -10px rgba(0, 0, 0, 0.55);
					}

					// img {
					// 	object-fit: contain;
					// 	// object-position: 0 70%;
					// }

					.leftImage {
						margin-right: 10px;
						padding: 5px;
					}

					.jumbo1 {
						background: linear-gradient(rgba(0, 0, 0, 0.3), rgba(0, 0, 0, 0.5)),
							url('https://scontent.fsin3-1.fna.fbcdn.net/v/t1.0-9/15027659_370613736610097_1460303554607900122_n.jpg?_nc_cat=107&_nc_oc=AQkRlwlDC8IaXGsrKrbSgPD8PlcVEtkkAOwd85OjpLSUTM2YMIOLbpA1FSghhB0atmg&_nc_ht=scontent.fsin3-1.fna&oh=b01baf74485a23d695d953a594aa990f&oe=5EC58659');
						background-size: cover;
						background-position: 110% 110%;
						/* opacity: 80%; */
						height: 800px;
						padding-top: 20%;
						padding-bottom: 20%;
					}

					.jumbo1words {
						color: white;
						font-family: "Permanent Marker", cursive;
					}

					.words {
						display: block;
						max-width: 900px;
						margin-top: 10px;
						font-size: 1.3rem;
						color: black;
					}
				`}</style>
			</Layout>
		);
	}
}
