// import Header from '../components/Header';
import Layout from '../components/MyLayout';
import Head from 'next/head';
import { Component } from 'react';
import CommentsForm from '../components/CommentsForm';

export default class AdminProfile extends Component {
	constructor(props) {
		super(props);
		this.state = {
			toggleAddComments   : false,
			toggleCheckBookings : false,
			username            : admin
		};
	}

	componentDidMount = () => {
		this.addComments();
		this.checkBookings();
	};

	addComments = () => {
		console.log('inside Comments');
		this.setState((prevState) => ({
			toggleAddComments : !prevState.toggleAddComments
		}));
		console.log(this.state.toggleAddComments);
	};

	checkBookings = () => {
		console.log('inside Check Bookings');
		this.setState((prevState) => ({
			toggleCheckBookings : !prevState.toggleCheckBookings
		}));
		console.log(this.state.toggleCheckBookings);
	};

	render() {
		return (
			<Layout title="Admin Profile">
				<Head>
					<title>Admin Profile</title>
					<link
						rel="stylesheet"
						href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css"
					/>
				</Head>

				<div className="card-space">
					<img
						className="imageHead"
						src="https://images.unsplash.com/photo-1474540412665-1cdae210ae6b?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60"
						alt=""
					/>
					<div className="container leftAlign">
						<h1>Admin Dashboard</h1>
						<h3>What would you like to do?</h3>
						<button onClick={this.addComments} className="btn btn-info">
							Add Comments to users
						</button>
						<button onClick={this.checkBookings} className="btn btn-info">
							Check Bookings
						</button>
						<hr />
					</div>
				</div>

				<div className="container">
					<div className="row">
						<div className="col-sm special">
							{this.state.toggleAddComments ? '' : <CommentsForm />}
							{this.state.toggleCheckBookings ? '' : <h1>display bookings</h1>}
						</div>
					</div>
				</div>

				<style jsx>
					{`
						.imageHead {
							// background-size: contain;
							width: 100%;
							height: 300px;
							object-fit: cover;
							object-position: 0 80%;
						}
						.card-space {
							padding-top: 3%;
						}
						.leftAlign {
							text-align: left;
							padding-top: 1%;
							margin-bottom: 1%;
						}
						.box {
							border: 1px solid black;
							padding: 2px;
							margin: 5px;
							text-align: center;
						}
						.special {
							// border: 1px solid blue;
							padding: 30px;
							margin: 5px;
							box-shadow: 9px 7px 25px -10px rgba(0, 0, 0, 0.75);
						}
						.btn {
							margin: 2px;
						}
					`}
				</style>
			</Layout>
		);
	}
}
