import Layout from '../components/MyLayout';
import Login from './login';
// import Link from 'next/link';
import fetch from 'isomorphic-unfetch';
import Head from 'next/head';

export default class Index extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			currentUser : '',
			delete      : false
		};
	}

	userState = (user) => {
		this.setState(
			{
				currentUser : user
			},
			() => {
				console.log('user logged in');
			}
		);
	};

	toLogout = () => {
		this.setState({ currentUser: '' });
	};

	toDelete = () => {
		this.setState({ delete: true });
	};

	render() {
		return (
			<Layout title="Home">
				<Head>
					<title>Your Yoga Journey</title>
					<link
						rel="stylesheet"
						href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css"
						integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T"
						crossOrigin="anonymous"
					/>
				</Head>
				<div className="container card-space">
					<div className="row">
						<div className="col-sm leftImage">
							Image
							{/* <img src="./images/test.jpg" /> */}
						</div>
						<div className="col-sm rightImage">Sign Up?</div>
					</div>
				</div>

				{/* Jumbo design  */}
				{/* <div className="jumbo1">
					<div className="container">
						<h1 className="display-4 jumbo1words">Image</h1>
					</div>
				</div> */}

				{/* {this.state.currentUser ? (
					<Kickstarterpath currentUser={this.state.currentUser} />
				) : (
					<Login userState={this.userState} />
				)} */}
				{/* <Kickstarterpath currentUser={this.state.currentUser} />{' '} */}

				{/* <img src="/images/test.jpg" /> */}
				<h1>Index page</h1>
				<style jsx>{`
					.col-sm {
						height: 700px;
						width: 100%;
						border: black 1px solid;
						background-color: grey;
					}

					.card-space {
						margin-top: 3%;
					}

					.leftImage {
						margin-right: 10px;
						padding: 5px;
					}

					.rightImage {
						margin-left: 10px;
						padding: 5px;
					}

					.jumbo1 {
						background: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5));
						background-size: cover;
						background-position: 10% 30%;
						/* opacity: 80%; */
						min-height: 300px;
						padding-top: 20%;
						padding-bottom: 20%;
					}

					.jumbo1words {
						color: white;
						font-family: "Permanent Marker";
					}
				`}</style>
			</Layout>
		);
	}
}
